import { Container, ThemeProvider, Typography, createTheme, TextField, TableContainer, LinearProgress, TableHead, Table, TableRow, TableCell, TableBody} from '@material-ui/core';
import axios from 'axios';
import React, { useEffect, useState } from 'react'
import { useNavigate } from 'react-router-dom';
import { CoinList } from '../config/api';
import { CryptoState } from '../CryptoContext';
import './CoinsTable.css'

const CoinsTable = () => {
    const [coins, setCoins] = useState([]);
    const [loading, setLoading] = useState(false);
    const [search, setSearch] = useState(" ");
    const navigate = useNavigate();

    const {currency} = CryptoState();

    const fetchCoins = async () =>  {
        setLoading(true);
        const {data} = await axios.get(CoinList(currency));
        setCoins(data)
        setLoading(false);
    };

    useEffect(() => {
        fetchCoins();
    }, [currency]);

    const darkTheme = createTheme({
        palette:{
          primary:{
            main:'#fff'
          },
          type:'dark',
        }
      });

      const handleSearch = () => {
        return coins.filter((coin) => 
            coin.name.toLowerCase().includes(search) ||
            coin.symbol.toLowerCase().includes(search)
        );
      };

  return (
    <>
    <ThemeProvider theme={darkTheme}>
        <Container className='coinstable-container'>
            <Typography className='coinstable-heading' variant='h4'>
            Cryptocurrency Prices by Market Cap
            </Typography>
            <TextField
            variant='outlined'
            label="Search Coins"
            className='searchbox'
            onChange={(e) =>setSearch(e.target.value)}
            />
            <TableContainer>
                {
                    loading ? (<LinearProgress className='linear-progress'/>) : (
                        <Table>
                            <TableHead className='table-head'>
                                <TableRow>
                                    {
                                        ["Coin", "Price", "24 change", "Market Cap"].map((head) => (
                                           <TableCell
                                                key={head}
                                                className="table-cell"
                                                align={head === "Coin" ? "" : "right"}>
                                                {head}
                                           </TableCell> 
                                        ))
                                    }
                                </TableRow>
                            </TableHead>
                            <TableBody>
                                
                                    {handleSearch().map((row) => {
                                        const profit =row.price_change_percentage_24h > 0;
                                        return(
                                        <TableRow
                                            key={row.name}
                                            className="inner-row"
                                            onClick={() => navigate(`/coins/${row.id}`)}>  
                                            <TableCell component="th" scope='row' className='table-cell-1'>
                                                <img
                                                src={row.image}
                                                alt={row.name}
                                                height="50"
                                                />
                                                <div className="crypto-names">
                                                    <span className=''>{row.symbol}</span>
                                                </div>
                                            </TableCell>
                                        </TableRow>
                                     
                                        )
                                    })}
                                
                            </TableBody>
                        </Table>
                    )
                }
            </TableContainer>
        </Container>
    </ThemeProvider>
    </>
  )
}

export default CoinsTable
